(function (res) {
    let isHtml = res.startsWith("<!DOCTYPE html>")
    if (isHtml) {
        let matchResult = baseUrl.match(new RegExp("pn|pixiv/novel"))
        if (matchResult == null) {
            return []
        }
        let id = baseUrl.match(new RegExp("\\d+"))[0]
        if (baseUrl.includes("/cache")) {
            res = JSON.parse(java.ajax(`https://api.furrynovel.ink/pixiv/novel/${id}/cache`))
            // 不获取缓存系列
            res.series = null
        } else {
            res = JSON.parse(java.ajax(`https://api.furrynovel.ink/pixiv/novel/${id}`))
        }
    } else {
        res = JSON.parse(res)
        if (res.total === 0) {
            return []
        }
    }

    let prop = {}
    //为了兼顾导入书架直接走详情页逻辑
    //这里不能直接用book.xxx 来复用搜索页处理结果
    prop['author'] = res.userName
    prop['count'] = book.wordCount
    prop['desc'] = res.desc
    prop['cover_url'] = `https://api.furrynovel.ink/proxy/pximg?url=${res.coverUrl}`

    if (res.series === undefined || res.series === null) {
        prop['name'] = res.title
        if (baseUrl.includes("/cache")) {
            prop['catalog'] = `https://api.furrynovel.ink/pixiv/novel/${res.id}/cache`
        } else {
            prop['catalog'] = `https://api.furrynovel.ink/pixiv/novel/${res.id}`
        }
        res.tags.unshift('单本')
    } else {
        prop['name'] = res.series.title
        res.tags.unshift('长篇')
        prop['catalog'] = `https://api.furrynovel.ink/pixiv/series/${res.series.id}`
    }
    prop['classes'] = res.tags.join(",")
    return prop
})(result)

ruleContent.content:
@js:
(function (res) {
    res = JSON.parse(res)
    let content = res.content
    if (res.series !== null && res.desc !== undefined && res.desc !== "") {
        content = res.desc + "\n" + "——————————\n".repeat(2) + content
    }

    // 获取 [uploadedimage:] 的图片链接
    //将存在的pixiv图片链接替换为可访问的直连
    if (res.images !== undefined && res.images !== null) {
        Object.keys(res.images).forEach((key) => {
            content = content.replace(`[uploadedimage:${key}]`, `<img src="https://pximg.furrynovel.ink/?url=${res.images[key].origin}">`)
        })
    }

    // // 获取 [pixivimage:] 的图片链接
    let rex = /\[pixivimage:(\d+)]/gm
    let matched = content.match(RegExp(rex))
    if (matched) {
        for (let i in matched) {
            let illustId = matched[i].match(RegExp("\\d+"))
            // let illustOriginal = `https://pixiv.cat/${illustId}.png`  // 已墙不可用
            let illustOriginal = `https://pixiv.re/${illustId}.png`
            // let illustOriginal = `https://pixiv.nl/${illustId}.png`
            content = content.replace(`${matched[i]}`, `<img src="${illustOriginal}">`)
        }
    }



    // 替换 Pixiv 章节标记符号 [newpage] [chapter:]
    content = content.replace(`[newpage]`, ``)
    matched = content.match(RegExp(/\[chapter:(.*?)]/gm))
    if (matched) {
        for (let i in matched) {
            let matched2 = matched[i].match(/\[chapter:(.*?)]/m)
            let chapter = matched2[1].trim()
            // 章节编号
            // if ((chapter.includes("第"))||(chapter.includes("章"))){
            //     // chapter = `${chapter}`
            // } else {
            //     chapter = `第${Number(i)+1}节 ${chapter}`
            // }
            content = content.replace(`${matched[i]}`, `${"<p>​<p/>".repeat(3)}${chapter}<p>​<p/>`)
        }
    }

    // 替换 Pixiv 跳转页面标记符号 [[jump:]]
    matched = content.match(/\[jump:(\d+)]/gm)
    if (matched) {
        for (let i in matched) {
            let page = matched[i].match(/\d+/)
            content = content.replace(`${matched[i]}`, `\n\n跳转至第${page}节`)
        }
    }

    // 替换 Pixiv 链接标记符号 [[jumpuri: > ]]
    matched = content.match(/\[\[jumpuri:(.*?)>(.*?)]]/gm)
    if (matched) {
        for (let i in matched) {
            let matched2 = matched[i].match(/\[\[jumpuri:(.*?)>(.*?)]]/m)
            let matchedText = matched2[0]
            let urlName = matched2[1].trim()
            let urlLink = matched2[2].trim()
            // 阅读不支持超链接
            //content = content.replace(`${matchedText}`, `<a href=${urlLink}>${urlName}</a>`)
            if (urlLink === urlName) {
                content = content.replace(`${matchedText}`, `${urlName}`)
            } else {
                content = content.replace(`${matchedText}`, `${urlName}: ${urlLink}`)
            }
        }
    }

    // 替换 Pixiv 注音标记符号 [[rb: > ]]
    matched = content.match(/\[\[rb:(.*?)>(.*?)]]/gm)
    if (matched) {
        for (let i in matched) {
            let matched2 = matched[i].match(/\[\[rb:(.*?)>(.*?)]]/m)
            let matchedText = matched2[0]
            let kanji = matched2[1].trim()
            let kana = matched2[2].trim()
            // kana为中文，则替换回《书名号》
            var reg = new RegExp("[\\u4E00-\\u9FFF]+","g");
            if (reg.test(kana)) {
                content = content.replace(`${matchedText}`, `${kanji}《${kana}》`)
            } else{
                // 阅读不支持 <ruby> <rt> 注音
                // content = content.replace(`${matchedText}`, `<ruby>${kanji}<rt>${kana}</rt></ruby>`)
                content = content.replace(`${matchedText}`, `${kanji}（${kana}）`)
            }
        }
    }

    return content
})(result)

ruleExplore.bookList:
@js:

function urlSearchUsers(username) {
    return `https://api.furrynovel.ink/pixiv/search/user/${username}`
}

function urlSeries(seriesId) {
    return `https://api.furrynovel.ink/pixiv/series/${seriesId}`
}

function urlNovelsDetailed(nidList) {
    return `https://api.furrynovel.ink/pixiv/novels?${nidList.map(v => "ids=" + v).join("&")}`
}

function urlUserDetailed(uidList) {
    return `https://api.furrynovel.ink/pixiv/users?${uidList.map(v => "ids=" + v).join("&")}`
}

function getAjaxJson(url) {
    return cacheGetAndSet(url, () => {
        // java.log(`url:${url}`)
        return JSON.parse(java.ajax(url))
    })
}

function getWebviewJson(url) {
    return cacheGetAndSet(url, () => {
        java.log(`url:${url}`)
        let html = java.webView(null, url, null)
        // java.log(`返回的html:${html}`)
        return JSON.parse((html.match(new RegExp(">\\[\\{.*?}]<"))[0].replace(">", "").replace("<", "")))
    })
}

function cacheGetAndSet(key, supplyFunc) {
    let v = cache.get(key)
    if (v === undefined || v === null) {
        v = JSON.stringify(supplyFunc())
        // 缓存10分钟
        cache.put(key, v, 600)
    }
    return JSON.parse(v)
}

// 存储seriesID
var seriesSet = new Set();

// 将多个长篇小说解析为一本书
function combineNovels(novels) {
    return novels.filter(novel => {
        //单本直接解析为一本书
        //需要判断是否为null
        if (novel.seriesId === undefined || novel.seriesId === null) {
            return true
        }

        //集合中没有该系列解析为一本书
        if (!seriesSet.has(novel.seriesId)) {
            seriesSet.add(novel.seriesId)
            return true
        }

        return false
    })
}

// 将小说的封面规则与详情地址替换
function handlerNovels(novels) {
    novels.forEach(novel => {
        novel.detailedUrl = `https://api.furrynovel.ink/pixiv/novel/${novel.id}`
        if (novel.seriesId !== undefined && novel.seriesId !== null) {
            novel.title = novel.seriesTitle
            novel.length = null

            java.log(`正在获取系列小说：${novel.seriesId}`)
            let series = getAjaxJson(urlSeries(novel.seriesId))
            // 后端目前没有系列的coverUrl字段
            // novel.coverUrl = `https://api.furrynovel.ink/proxy/pximg?url=${series.imageUrl}`
            novel.coverUrl = `https://api.furrynovel.ink/proxy/pximg?url=${series.novels[0].coverUrl}`
            if (series.caption === "") {
                let firstNovels = getAjaxJson(urlNovelsDetailed([series.novels[0].id]))
                if (firstNovels.length > 0) {
                    novel.desc = firstNovels[0].desc
                } else {
                    novel.desc = "该小说可能部分章节因为权限或者被删除无法查看"
                }
            } else {
                novel.desc = series.caption
            }

            //如果没有标签 取第一章的tag
            if (series.tags.length === 0) {
                // 系列至少会有一章
                novel.tags = series.novels[0].tags
            } else {
                novel.tags = series.tags
            }

            if (novel.tags === undefined) {
                novel.tags = []
            }
            novel.tags.unshift("长篇")


        } else {
            novel.tags.unshift("单本")
            novel.coverUrl = `https://api.furrynovel.ink/proxy/pximg?url=${novel.coverUrl}`
        }

        novel.tags = novel.tags.join(",")
    })
    return novels
}

/**
 * @params arr 传入的源数组
 * @params length 需要获取的元素的个数
 */
function randomChoseArrayItem(arr, length) {
    let copyArr = JSON.parse(JSON.stringify(arr))
    let newArr = [];
    for (let i = 0; i < length; i++) {
        let index = Math.floor(Math.random() * copyArr.length);
        let item = copyArr[index];
        newArr.push(item)
        copyArr.splice(index, 1)
    }
    return newArr.reverse()
}


function handlerRecommendUsers() {
    const MAX_FETCH_USER_NUMBER = 2;

    return () => {
        let novelList = []
        let userIds = JSON.parse(result).map(i => i.id)
        // java.log(`用户id个数:${userIds.length}`)
        if (userIds.length > MAX_FETCH_USER_NUMBER) {
            userIds = randomChoseArrayItem(userIds, MAX_FETCH_USER_NUMBER);
        }

        // java.log(`查询的用户Ids:${userIds}`)

        let usersInfo = getWebviewJson(urlUserDetailed(userIds))
        // java.log(`返回的${JSON.stringify(usersInfo)}`)
        let queryNovelIds = []
        // java.log(`${JSON.stringify(usersInfo)}`)
        usersInfo.filter(user => user.novels && user.novels.length > 0)
            .map(user => user.novels)
            // 将list展平[1,2,3]变为1,2,3 添加到novelList中
            .forEach(novels => {
                novels.forEach(novel => {
                    queryNovelIds.push(novel)
                })
            })
        // 暂时限制最大获取数量
        if (queryNovelIds.length > 10) {
            queryNovelIds = randomChoseArrayItem(queryNovelIds, 10)
        }
        novelList = getWebviewJson(urlNovelsDetailed(queryNovelIds))
        return handlerNovels(combineNovels(novelList))
    }
}

function handlerFollowLatest() {
    return () => {
        let resp = JSON.parse(result)
        return handlerNovels(combineNovels(resp))
    }
}

function handlerFactory() {
    if (baseUrl.indexOf("/fav/user") !== -1) {
        return handlerRecommendUsers()
    }

    if (baseUrl.indexOf("/pixiv/novels/recent") !== -1) {
        return handlerFollowLatest()
    }

}

(() => {
    return handlerFactory()()
})()

ruleSearch.bookList:
@js:

function urlSearchUsers(username) {
    return `https://api.furrynovel.ink/pixiv/search/user/${username}`
}

function urlUserDetailed(uidList) {
    return `https://api.furrynovel.ink/pixiv/users?${uidList.map(v => "ids[]=" + v).join("&")}`
}

function urlNovelsDetailed(nidList) {
    return `https://api.furrynovel.ink/pixiv/novels?${nidList.map(v => "ids[]=" + v).join("&")}`
}

function urlSeries(seriesId) {
    return `https://api.furrynovel.ink/pixiv/series/${seriesId}`
}

function cacheGetAndSet(key, supplyFunc) {
    let v = cache.get(key)
    if (v === undefined || v === null) {
        v = JSON.stringify(supplyFunc())
        // 缓存10分钟
        cache.put(key, v, 600)
    }
    return JSON.parse(v)
}


function getUser(username, exactMatch) {
    // 修复传入object的bug
    username = String(username)
    let resp = getAjaxJson(urlSearchUsers(username))
    if (resp.users.length === 0) {
        return []
    }
    if (!exactMatch) {
        return resp.users
    }
    // 只返回用户名完全一样的用户
    return resp.users.filter(user => {
        return user.name === username
    })
}

function getAjaxJson(url) {
    return cacheGetAndSet(url, () => {
        return JSON.parse(java.ajax(url))
    })
}

function getWebviewJson(url) {
    return cacheGetAndSet(url, () => {
        let html = java.webView(null, url, null)
        return JSON.parse((html.match(new RegExp(">\\[\\{.*?}]<"))[0].replace(">", "").replace("<", "")))
    })
}

// 包含所有小说数据
function getUserDetailedList(uidList) {
    // java.log(`UIDLIST:${JSON.stringify(uidList)}`)
    return getWebviewJson(urlUserDetailed(uidList))
}

function getNovels(nidList) {
    let page = Number(java.get("page"))
    // 分页
    let list = nidList.slice((page - 1) * 20, page * 20)
    if (list.length === 0) {
        return []
    }

    // java.log(`NIDURL:${urlNovelsDetailed(list)}`)
    return getWebviewJson(urlNovelsDetailed(list))
}

// 存储seriesID
var first = true;
var seriesSet = {
    keywords: "Linpx:Search",
    has: (value) => {
        let page = Number(java.get("page"))
        if (page === 1 && first) {
            first = false
            cache.deleteMemory(this.keywords)
            return false
        }

        let v = cache.getFromMemory(this.keywords)
        if (v === undefined || v === null) {
            return false
        }
        let set = new Set(JSON.parse(v))
        return set.has(value)
    },

    add: (value) => {
        let v = cache.getFromMemory(this.keywords)
        if (v === undefined || v === null) {
            cache.putMemory(this.keywords, JSON.stringify([value]))

        } else {
            let arr = JSON.parse(v)
            if (typeof arr === "string") {
                arr = Array(arr)
            }
            arr.push(value)
            cache.putMemory(this.keywords, JSON.stringify(arr))
        }
    },
};

// 将多个长篇小说解析为一本书
function combineNovels(novels) {
    return novels.filter(novel => {
        //单本直接解析为一本书
        //需要判断是否为null
        if (novel.seriesId === undefined || novel.seriesId === null) {
            return true
        }

        //集合中没有该系列解析为一本书
        if (!seriesSet.has(novel.seriesId)) {
            seriesSet.add(novel.seriesId)
            return true
        }

        return false
    })
}

// 将小说的封面规则与详情地址替换
function formatNovels(novels) {
    novels.forEach(novel => {
        novel.detailedUrl = `https://api.furrynovel.ink/pixiv/novel/${novel.id}`
        if (novel.seriesId !== undefined && novel.seriesId !== null) {
            novel.title = novel.seriesTitle
            novel.length = null

            let series = getAjaxJson(urlSeries(novel.seriesId))
            // 后端目前没有系列的coverUrl字段
            // novel.coverUrl = `https://api.furrynovel.ink/proxy/pximg?url=${series.imageUrl}`
            novel.coverUrl = `https://api.furrynovel.ink/proxy/pximg?url=${series.novels[0].coverUrl}`
            if (series.caption === "") {
                let firstNovels = getAjaxJson(urlNovelsDetailed([series.novels[0].id]))
                if (firstNovels.length > 0) {
                    novel.desc = firstNovels[0].desc
                } else {
                    novel.desc = "该小说可能部分章节因为权限或者被删除无法查看"
                }
            } else {
                novel.desc = series.caption
            }

            //如果没有标签 取第一章的tag
            if (series.tags.length === 0) {
                // 系列至少会有一章
                novel.tags = series.novels[0].tags
            } else {
                novel.tags = series.tags
            }

            if (novel.tags === undefined) {
                novel.tags = []
            }
            novel.tags.unshift("长篇")


        } else {
            if (novel.tags === undefined) {
                novel.tags = []
            }
            novel.tags.unshift("单本")
            novel.coverUrl = `https://api.furrynovel.ink/proxy/pximg?url=${novel.coverUrl}`
        }

        novel.tags = novel.tags.join(",")
    })
    return novels
}

function findUserNovels(username) {
    let novelList = []
    // 查询用户
    let userArr = getUser(username, true)
    // 获取用户所有小说
    let uidList = userArr.filter(user => {
        return user.novels.length > 0
    }).map(user => user.id)

    if (uidList.length > 0) {
        let list = getUserDetailedList(uidList)
        let nidList = []
        // 从两层数组中提取novelsId
        list.forEach(user => {
            user.novels
                // 按id降序排序-相当于按时间降序排序
                .reverse()
                .forEach(nid => nidList.push(nid))
        })
        getNovels(nidList).forEach(novel => {
            novelList.push(novel)
        })
    }
    return novelList
}

(function (res) {
    res = JSON.parse(res)
    let novels = []
    findUserNovels(java.get("key")).forEach(v => {
        novels.push(v)
    })
    novels = novels.concat(res.novels)
    // 返回空列表中止流程
    if (novels.length === 0) {
        return []
    }
    return formatNovels(combineNovels(novels))
}(result))

